import * as R from 'ramda'

import phones from '../api/mockPhones'

const fetchPhones = async () => {
	return new Promise((resolve, reject) => {
      resolve(phones)
      // reject('error')
	})
}

const loadMorePhones = async ({offset}) => {
	return new Promise((resolve, reject) => {
      resolve(phones)
      // reject('error')
	})
}

const fetchPhoneById = async (id) => {
	return new Promise((resolve, reject) => {
		const phone= R.find(R.propEq('id', id), phones)
		resolve(phone)
	})
}
export { fetchPhones, loadMorePhones, fetchPhoneById }
